mapper.select:
SELECT s.customer_ID AS shop_id ,
${" tmc.`channel_model` AS model,ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS quantity"}
${"tmc.`channel_model` AS model,ROUND(COALESCE(SUM(s.`quantity`),0)) AS quantity"}
FROM t_sale s JOIN customer_info ci ON ci.`customer_ID`=s.`customer_ID` JOIN party pa ON pa.party_id = ci.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=ci.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1
AND s.datadate BETWEEN #{beginDate} AND #{endDate} AND pr.PRODUCT_SPEC_ID LIKE '%BASIC LED%'
${"group By ci.`customer_id`,tm.hq_model"}
${"group By ci.`customer_id`,tmc.`channel_model`"}

SELECT s.customer_id AS shop_id ,
${"ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS quantity,COALESCE(SUM(s.`h_amount`),0) AS amount"}
${"ROUND(COALESCE(SUM(s.`quantity`),0)) AS quantity,COALESCE(SUM(s.`amount`),0) AS amount"}
FROM t_sale s JOIN customer_info p ON p.`customer_ID`=s.`customer_id`
JOIN party pa ON pa.party_id = p.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1
AND s.datadate BETWEEN #{beginDate} AND #{endDate} AND pr.PRODUCT_SPEC_ID LIKE '%BASIC LED%' GROUP BY s.customer_id

SELECT
${"DISTINCT tm.hq_model AS model ,COALESCE(SUM(t.`h_price`),0) AS price"}
${"DISTINCT tmc.`channel_model` AS model ,COALESCE(SUM(t.`price`),0) AS price"}
, COUNT(DISTINCT t.`customer_id`) AS shop FROM t_sale t
JOIN customer_info ci ON t.`customer_id`=ci.`CUSTOMER_ID` JOIN party pa ON pa.party_id = ci.party_id JOIN t_modelmap tm ON tm.`branch_model`=t.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=ci.customer_id
JOIN product p ON p.product_model=tm.`hq_model` AND p.head_type_id =1
AND p.PRODUCT_SPEC_ID LIKE '%BASIC LED%' AND t.datadate BETWEEN #{beginDate} AND #{endDate}
${"group By tm.hq_model"}
${"group By tmc.`channel_model`"}

 
 
 
 		SELECT st.`target_id` AS shop_id,
		
		${"SUM(COALESCE(st.`h_amount`,0) )AS targetSum,
		SUM(COALESCE(st.`h_tz_amount`,0)) AS challengeSum"}
		
		
		
		${"SUM(COALESCE(st.`amount`,0) )AS targetSum,
		SUM(COALESCE(st.`tz_amount`,0)) AS challengeSum"}
		
		
		
		FROM t_target
		st
		JOIN
		customer_info ci ON ci.`customer_ID`=st.`target_id` AND st.`class_id`=3
		JOIN party pa ON pa.party_id = ci.party_id
		
		AND st.type=#{type}
		AND  st.`datadate`  BETWEEN  #{tBeginDate}   AND  #{tEndDate}
		GROUP  BY  ci.`CUSTOMER_ID`

SELECT s.`customer_id` shop_id,
${"COALESCE(SUM(s.amount),0) AS saleSum ,ROUND(COALESCE(SUM(s.quantity),0)) AS saleQty"}
${"COALESCE(SUM(s.h_amount),0) AS saleSum ,ROUND(COALESCE(SUM(s.h_quantity),0)) AS saleQty"}
FROM t_sale s FORCE INDEX(idx_sale_shopdatadate) JOIN customer_info si ON s.`customer_id`=si.`customer_ID` JOIN party pa ON pa.`PARTY_ID`=si.`PARTY_ID`
JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=si.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1
AND s.datadate BETWEEN #{beginDate} AND #{endDate} GROUP BY s.customer_id

SELECT s.`customer_id` shop_id,
${"COALESCE(SUM(s.amount),0) AS saleSum ,ROUND(COALESCE(SUM(s.quantity),0)) AS saleQty"}
${"COALESCE(SUM(s.h_amount),0) AS saleSum ,ROUND(COALESCE(SUM(s.h_quantity),0)) AS saleQty"}
FROM t_sale s FORCE INDEX(idx_sale_shopdatadate) JOIN customer_info si ON s.`customer_id`=si.`customer_ID` JOIN party pa ON pa.`PARTY_ID`=si.`PARTY_ID`
JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=si.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =2
AND s.datadate BETWEEN #{beginDate} AND #{endDate} GROUP BY s.customer_id

SELECT
${"tmc.`channel_model` model"}
${"tm.`hq_model` model"}
,ROUND(COALESCE(SUM(t.`quantity`),0)) AS quantity FROM t_inventory t JOIN customer_info ci ON ci.`CUSTOMER_ID`=t.`customer_id`
JOIN party pa ON pa.party_id = ci.party_id JOIN t_modelmap tm ON tm.`branch_model`=t.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=ci.customer_id JOIN product pr ON pr.product_model=tm.`hq_model`
AND t.class_id=1 AND pr.PRODUCT_SPEC_ID LIKE #{spec} AND pr.head_type_id=1
AND t.datadate BETWEEN #{beginDate} AND #{endDate}
${"group By tm.hq_model"}
${"group By tmc.`channel_model`"}

