SELECT sv.PARTY_ID AS PARTY_ID,sv.PARTY_NAME AS PARTY_NAME, sv.product_line AS product_line,
COALESCE(SUM(sv.saleQty),0) AS saleQty ,COALESCE(SUM(tv.targetQty ),0) AS targetQty FROM (SELECT s.`customer_id` shop_id,s.`model`,pa.`PARTY_ID`,pa.`PARTY_NAME`,pr.`product_line`,
${"ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS saleQty"}
${"ROUND(COALESCE(SUM(s.`quantity`),0)) AS saleQty"}
FROM t_sale s JOIN customer_info p ON p.`customer_ID`=s.`customer_id` JOIN party pa ON pa.party_id = p.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1 JOIN core_product cp ON cp.`product_line`=pr.`product_line` AND cp.`country_id`=pa.`COUNTRY_ID`
AND s.`datadate` BETWEEN #{beginDate} AND #{endDate} GROUP BY s.`customer_id`, s.`model`,pr.`product_line`
)sv LEFT JOIN (SELECT tst.`product_model`,tst.`target_id` AS targetId,
${"COALESCE((tst.`h_quantity`),0) AS targetQty "}
${"COALESCE((tst.`quantity`),0) AS targetQty "}
FROM t_target tst WHERE tst.`class_id`=7 AND tst.`datadate` BETWEEN #{tBeginDate} AND #{tEndDate} ) tv ON sv.shop_id=tv.targetId AND sv.`model`=tv.product_model
GROUP BY sv.`PARTY_ID`, sv.`product_line`

SELECT sv.product_line AS product_line,COALESCE(SUM(SV.saleSum),0) AS saleSum,COALESCE(SUM(tv.targetSum),0) AS targetSum FROM
(SELECT s.`model`,s.`customer_id` shop_id ,pr.`product_line`,
${"ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS saleSum"}
${"ROUND(COALESCE(SUM(s.`quantity`),0)) AS saleSum"}
FROM t_sale s JOIN customer_info p ON p.`customer_ID`=s.`customer_id` JOIN party pa ON pa.party_id = p.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1 JOIN core_product cp ON cp.`product_line`=pr.`product_line` AND cp.`country_id`=pa.`COUNTRY_ID`
AND s.datadate BETWEEN #{beginDate} AND #{endDate}
GROUP BY s.`model`,s.`customer_id`,pr.`product_line` )sv LEFT JOIN (SELECT tst.`product_model`,tst.`target_id` AS targetId,
${" COALESCE((tst.`h_amount`),0) AS targetSum"}
${" COALESCE((tst.`amount`),0) AS targetSum"}
FROM t_target tst WHERE tst.`class_id`=7 AND tst.`datadate` BETWEEN #{tBeginDate} AND #{tEndDate} ) tv ON sv.shop_id=tv.targetId AND sv.`model`=tv.product_model
GROUP BY sv.`product_line`

SELECT pr.`product_line`,
${" tm.`hq_model` AS model, round(COALESCE(SUM(s.`h_quantity`),0)) AS quantity"}
${" tmc.`channel_model` AS model, round(COALESCE(SUM(s.`quantity`),0)) AS quantity"}
FROM t_sale s JOIN customer_info p ON p.`customer_ID`=s.`customer_id` JOIN party pa ON pa.party_id = p.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1 JOIN core_product cp ON cp.`product_line`=pr.`product_line` AND cp.`country_id`=pa.country_id
AND s.datadate BETWEEN #{beginDate} AND #{endDate}
${"group By pr.`product_line`,tm.hq_model"}
${"group By pr.`product_line`,tmc.`channel_model`"}

		SELECT pr.`product_line`,
		
		
		${"
		tm.`hq_model` AS model"}
		
		
		
		${"
		tmc.`channel_model` AS model"}
		
		
		FROM t_sale s FORCE INDEX(idx_sale_shopdatadate)
		JOIN customer_info p ON
		p.`customer_ID`=s.`customer_id`
		JOIN party pa ON pa.party_id = p.party_id
		JOIN t_modelmap tm ON tm.`branch_model`=s.model   AND tm.`party_id`=pa.`COUNTRY_ID`
		JOIN  t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id`
		AND tmc.`customer_id`=p.customer_id
		JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1
		
		AND s.datadate BETWEEN #{beginDate} AND #{endDate}
		
		${"group By pr.`product_line`,tm.hq_model"}
		
		
		${"group By pr.`product_line`,tmc.`channel_model`"}

SELECT sv.product_line AS product_line, sv.userId,sv.userName, COALESCE(SUM(sv.saleQty),0) AS saleQty,COALESCE(SUM(tv.targetQty),0) AS targetQty FROM
( SELECT t.`model`,ci.`customer_id`,pr.`product_line`,ul.`USER_login_id` AS userId,ul.`USER_NAME` AS userName,
${"ROUND(COALESCE(SUM(t.`h_quantity`),0)) AS saleQty"}
${"ROUND(COALESCE(SUM(t.`quantity`),0)) AS saleQty"}
FROM t_sale t JOIN customer_info ci ON t.`customer_id`=ci.`customer_id`
JOIN shop_info si ON ci.`customer_id`=si.`customer_id` JOIN `shop_saler_mapping` ssm ON ssm.`shop_id`=t.`shop_id` JOIN user_login ul ON ul.`USER_LOGIN_ID`=ssm.`user_login_id` JOIN party pa ON pa.party_id = si.party_id JOIN t_modelmap tm ON tm.`branch_model`=t.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=ci.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1 JOIN core_product cp ON cp.`product_line`=pr.`product_line`
AND t.datadate BETWEEN #{beginDate} AND #{endDate}
AND ssm.salertype='0' AND cp.`country_id`=ul.`PARTY_ID`
GROUP BY t.`model`,pr.product_line,t.`shop_id`,ul.`USER_login_id`
)sv
LEFT JOIN (SELECT tst.`product_model`,tst.`target_id` AS targetId,
${"COALESCE((tst.`h_quantity`),0) AS targetQty"}
${"COALESCE((tst.`quantity`),0) AS targetQty"}
FROM t_target tst WHERE tst.`class_id`=7 AND tst.`datadate` BETWEEN #{tBeginDate} AND #{tEndDate} ) tv ON sv.customer_id=tv.targetId AND sv.`model`=tv.product_model
GROUP BY sv.userId,sv.product_line

SELECT pr.`product_line`,ul.`USER_login_id` AS userId,ul.`USER_NAME` AS userName,
${" tm.`hq_model` as model,round(COALESCE(SUM(t.`h_quantity`),0)) AS saleQty"}
${"tmc.`channel_model` as model,round(COALESCE(SUM(t.`quantity`),0)) AS saleQty"}
FROM t_sale t JOIN customer_info ci ON t.`customer_id`=ci.`customer_ID`
JOIN shop_info si ON ci.`customer_ID`=si.`customer_ID` JOIN `shop_saler_mapping` ssm ON ssm.`shop_id`=t.`shop_id` JOIN user_login ul ON ul.`USER_LOGIN_ID`=ssm.`user_login_id` JOIN party pa ON pa.party_id = si.party_id JOIN t_modelmap tm ON tm.`branch_model`=t.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=ci.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1 JOIN core_product cp ON cp.`product_line`=pr.`product_line`
AND t.datadate BETWEEN #{beginDate} AND #{endDate} AND ssm.salertype='0' AND cp.`country_id`=ul.`PARTY_ID`
${"group By ssm.`user_login_id`,tm.hq_model, pr.`product_line`"}
${"group By ssm.`user_login_id`,tmc.`channel_model`, pr.`product_line`"}

