SELECT
${"COALESCE( SUM(s.amount),0)AS saleSum ,ROUND(COALESCE(SUM(s.`quantity`),0)) AS saleQty"}
${"COALESCE( SUM(s.h_amount),0)AS saleSum ,ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS saleQty"}
FROM t_sale s FORCE INDEX(idx_sale_shopdatadate)
JOIN customer_info p ON p.`customer_ID`=s.`customer_id`
JOIN party pa ON pa.party_id = p.party_id
JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1
AND s.datadate BETWEEN #{beginDate} AND #{endDate}

SELECT
${"COALESCE( SUM(s.amount),0)AS saleSum ,ROUND(COALESCE(SUM(s.`quantity`),0)) AS saleQty"}
${"COALESCE( SUM(s.h_amount),0)AS saleSum ,ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS saleQty"}
FROM t_sale s FORCE INDEX(idx_sale_shopdatadate)
JOIN customer_info p ON p.`customer_ID`=s.`customer_id`
JOIN party pa ON pa.party_id = p.party_id
JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =2
AND s.datadate BETWEEN #{beginDate} AND #{endDate}

SELECT
${"COALESCE(SUM(st.h_amount),0) AS targetSum, ROUND(COALESCE(SUM(st.`h_quantity`),0)) AS targetQty, COALESCE(SUM(st.h_tz_amount),0) AS challengeSum, ROUND(COALESCE(SUM(st.`h_tz_quantity`),0)) AS challengeQty"}
${"COALESCE(SUM(st.amount),0) AS targetSum, ROUND(COALESCE(SUM(st.`quantity`),0)) AS targetQty, COALESCE(SUM(st.tz_amount),0) AS challengeSum, ROUND(COALESCE(SUM(st.`tz_quantity`),0)) AS challengeQty"}
FROM t_target st JOIN customer_info p ON p.`customer_ID`=st.`target_id` AND st.`class_id`=3 JOIN party pa ON pa.party_id = p.party_id
AND st.type=#{type} AND st.`datadate` BETWEEN #{tBeginDate} AND #{tEndDate}

SELECT pa.`PARTY_ID`,
${" COALESCE(SUM(s.`h_amount`),0) AS targetSum,COALESCE(SUM(s.h_tz_amount),0) AS challengeSum"}
${" COALESCE(SUM(s.`amount`),0) AS targetSum,COALESCE(SUM(s.tz_amount),0) AS challengeSum"}
FROM t_target s JOIN customer_info p ON p.`customer_ID`=s.`target_id` AND s.`class_id`=3
JOIN party pa ON pa.party_id = p.party_id
AND s.type=#{type} AND s.datadate BETWEEN #{tBeginDate} AND #{tEndDate} group by pa.party_id

		
		SELECT 
		
			${"DISTINCT tm.hq_model AS model 
		,COALESCE(SUM(t.`h_price`),0) AS price"}
		
		
		
		${"DISTINCT  tmc.`channel_model` AS model 
		,COALESCE(SUM(t.`price`),0) AS price"}
		
		,
		COUNT(DISTINCT t.`customer_id`) AS shop FROM
		t_sale t  

		JOIN customer_info  ci ON t.`customer_id`=ci.`CUSTOMER_ID`
		JOIN
		party pa ON pa.party_id = ci.party_id  
		JOIN t_modelmap tm ON tm.`branch_model`=t.model   AND tm.`party_id`=pa.`COUNTRY_ID`
		JOIN  t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id`
		AND tmc.`customer_id`=ci.customer_id
		JOIN product p ON p.product_model=tm.`hq_model` AND p.head_type_id =1
		
		AND p.PRODUCT_SPEC_ID LIKE #{spec}
		AND t.datadate BETWEEN #{beginDate} AND #{endDate} 
		
		${"group By tm.hq_model"}
		
		
		${"group By tmc.`channel_model`"}

			SELECT 
		
			${"DISTINCT tm.hq_model AS model 
		,COALESCE(SUM(t.`h_price`),0) AS price"}
		
		
		
		${"DISTINCT  tmc.`channel_model` AS model 
		,COALESCE(SUM(t.`price`),0) AS price"}
		
		,
		COUNT(DISTINCT t.`customer_id`) AS shop FROM
		t_sale t  

		JOIN customer_info  ci ON t.`customer_id`=ci.`CUSTOMER_ID`
		JOIN
		party pa ON pa.party_id = ci.party_id  
		JOIN t_modelmap tm ON tm.`branch_model`=t.model   AND tm.`party_id`=pa.`COUNTRY_ID`
		JOIN  t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id`
		AND tmc.`customer_id`=ci.customer_id
		JOIN product p ON p.product_model=tm.`hq_model` AND p.head_type_id =2
		
		AND p.PRODUCT_SPEC_ID LIKE #{spec}
		AND t.datadate BETWEEN #{beginDate} AND #{endDate} 
		
		${"group By tm.hq_model"}
		
		
		${"group By tmc.`channel_model`"}

		
	SELECT ci.`customer_ID` AS shop_id
		,
		 
  ${" tm.hq_model AS model,ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS
		quantity"}
  
   
  ${"tmc.`channel_model` AS model,ROUND(COALESCE(SUM(s.`quantity`),0)) AS
		quantity"}
  

		FROM t_sale s 
		JOIN customer_info ci
		ON ci.`customer_ID`=s.`customer_ID`
		JOIN party pa ON
		pa.party_id = ci.party_id
		JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID`
		JOIN  t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id`
		AND tmc.`customer_id`=ci.customer_id
		JOIN product pr ON pr.product_model=tm.`hq_model`  AND pr.head_type_id =1
		
		AND s.datadate BETWEEN #{beginDate} AND #{endDate}
		AND pr.PRODUCT_SPEC_ID LIKE #{spec}
		
		${"group By ci.`customer_id`,tm.hq_model"}
		
		
		${"group By ci.`customer_id`,tmc.`channel_model`"}

			
	SELECT ci.`customer_ID` AS shop_id
		,
		 
  ${" tm.hq_model AS model,ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS
		quantity"}
  
   
  ${"tmc.`channel_model` AS model,ROUND(COALESCE(SUM(s.`quantity`),0)) AS
		quantity"}
  

		FROM t_sale s 
		JOIN customer_info ci
		ON ci.`customer_ID`=s.`customer_ID`
		JOIN party pa ON
		pa.party_id = ci.party_id
		JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID`
		JOIN  t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id`
		AND tmc.`customer_id`=ci.customer_id
		JOIN product pr ON pr.product_model=tm.`hq_model`  AND pr.head_type_id =2
		
		AND s.datadate BETWEEN #{beginDate} AND #{endDate}
		AND pr.PRODUCT_SPEC_ID LIKE #{spec}
		
		${"group By ci.`customer_id`,tm.hq_model"}
		
		
		${"group By ci.`customer_id`,tmc.`channel_model`"}

SELECT s.customer_id AS shop_id ,
${"ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS quantity,COALESCE(SUM(s.`h_amount`),0) AS amount"}
${"ROUND(COALESCE(SUM(s.`quantity`),0)) AS quantity,COALESCE(SUM(s.`amount`),0) AS amount "}
FROM t_sale s JOIN customer_info p ON p.`customer_ID`=s.`customer_id` JOIN party pa ON pa.party_id = p.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1
AND s.datadate BETWEEN #{beginDate} AND #{endDate}
AND (pr.PRODUCT_SPEC_ID LIKE '%DIGITAL%' OR pr.PRODUCT_SPEC_ID LIKE '%INTERNET%' OR pr.PRODUCT_SPEC_ID LIKE '%SMART%' OR pr.PRODUCT_SPEC_ID LIKE '%UHD%' OR pr.PRODUCT_SPEC_ID LIKE '%CURVE%' OR pr.PRODUCT_SPEC_ID LIKE '%QUHD%' ) GROUP BY s.customer_id

SELECT s.customer_id AS shop_id ,
${"ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS quantity,COALESCE(SUM(s.`h_amount`),0) AS amount"}
${"ROUND(COALESCE(SUM(s.`quantity`),0)) AS quantity,COALESCE(SUM(s.`amount`),0) AS amount "}
FROM t_sale s JOIN customer_info p ON p.`customer_ID`=s.`customer_id` JOIN party pa ON pa.party_id = p.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =2
AND s.datadate BETWEEN #{beginDate} AND #{endDate}
AND (pr.product_type LIKE '%Fission%' OR pr.product_type LIKE '%Window%'
)
GROUP BY s.customer_id

SELECT s.customer_id AS shop_id ,
${"round(COALESCE(SUM(s.`h_quantity`),0)) AS quantity,COALESCE(SUM(s.`h_amount`),0) AS amount"}
${"round(COALESCE(SUM(s.`quantity`),0)) AS quantity,COALESCE(SUM(s.`amount`),0) AS amount"}
FROM t_sale s JOIN customer_info p ON p.`customer_ID`=s.`customer_id` JOIN party pa ON pa.party_id = p.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=p.customer_id JOIN product pr ON pr.product_model=tm.`hq_model` AND pr.head_type_id =1
AND s.datadate BETWEEN #{beginDate} AND #{endDate}
AND (pr.PRODUCT_SPEC_ID LIKE #{spec}
) AND pr.head_type_id =1 GROUP BY s.customer_id

