		SELECT 
		
		${"tm.hq_model AS model
		"}
		

		
		${"tmc.`channel_model` AS model
		"}
		,
		cp.`product_line`  FROM
		`core_product` cp
		JOIN product p ON p.`product_line`=cp.`product_line`  AND p.head_type_id =1
		JOIN t_modelmap tm ON tm.`hq_model`=p.`PRODUCT_model`  AND tm.`party_id`=#{countryId}
		JOIN  t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id`
		WHERE country_id=#{countryId}

SELECT
${"tm.hq_model AS model "}
${"tmc.`channel_model` AS model "} ,
cp.`product_line` FROM `core_product` cp JOIN product p ON p.`product_line`=cp.`product_line` AND p.head_type_id =1 JOIN t_modelmap tm ON tm.`hq_model`=p.product_model AND tm.`party_id`=#{countryId} JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` JOIN `t_inventory` ti ON tm.`branch_model`=ti.`model` AND ti.country_id=tm.party_id WHERE cp.country_id=#{countryId}

SELECT
${"tm.hq_model AS model "}
${"tmc.`channel_model` AS model "} , cp.`product_line` FROM `core_product` cp JOIN product p ON p.`product_line`=cp.`product_line` AND p.head_type_id =1
JOIN t_modelmap tm ON tm.`hq_model`=p.product_model AND tm.`party_id`=#{countryId} JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` JOIN sample_record ts ON tm.`branch_model`=ts.`model` and ts.country_id=tm.party_id WHERE cp.country_id=#{countryId}
AND ts.`class_id`=1

SELECT cp.`product_line`FROM `core_product` cp
WHERE
country_id=#{countryId}

SELECT p.`product_line` ,
${"tm.hq_model AS model,ROUND(COALESCE(SUM(s.`h_quantity`),0)) AS quantity, COALESCE(SUM(s.`h_amount`),0) AS amount "}
${"tmc.`channel_model` AS model,ROUND(COALESCE(SUM(s.`quantity`),0)) AS quantity, COALESCE(SUM(s.`amount`),0) AS amount "}
FROM t_sale s
JOIN customer_info si ON s.`customer_ID`=si.`customer_id`
JOIN party pa ON pa.party_id = si.party_id
JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=si.customer_id JOIN product p ON p.product_model=tm.`hq_model` AND p.head_type_id =1 JOIN core_product cp ON p.`product_line`=cp.`product_line`
AND cp.`country_id`=#{countryId} AND s.`datadate` BETWEEN #{beginDate} AND #{endDate}
${"group By tm.hq_model"}
${"group By tmc.`channel_model`"} ,p.`product_line`

SELECT p.`product_line`,
${"ROUND(COALESCE(SUM(t.`h_quantity`),0)) AS quantity, COALESCE(SUM(t.`h_amount`),0) AS amount"}
${"ROUND(COALESCE(SUM(t.`quantity`),0)) AS quantity, COALESCE(SUM(t.`amount`),0) AS amount"}
FROM t_sale s
JOIN customer_info si ON s.`customer_ID`=si.`customer_id`
JOIN party pa ON pa.party_id = si.party_id JOIN t_modelmap tm ON tm.`branch_model`=s.model AND tm.`party_id`=pa.`COUNTRY_ID` JOIN t_modelmap_channel tmc ON tmc.`branch_model`=tm.`branch_model` AND tmc.`party_id`=tm.`party_id` AND tmc.`customer_id`=si.customer_id JOIN product p ON p.product_model=tm.`hq_model` AND p.head_type_id =1
JOIN core_product cp ON cp.`product_line`=p.`product_line` AND cp.`country_id`=pa.country_id
LEFT JOIN `t_target` t ON t.`target_id`=s.`shop_id` AND t.`class_id`=7
AND t.`product_model`=s.model AND t.`datadate` BETWEEN #{tBeginDate} AND #{tEndDate}
AND s.`datadate` BETWEEN #{beginDate} AND #{endDate}
GROUP BY p.product_line
